// Master_User_Configから顧客リストを作成
const clients = $input.all();

// drive_folder_idとsheet_idが両方設定されている顧客のみ抽出
const validClients = clients.filter(item => {
  const json = item.json;
  return json.drive_folder_id && json.sheet_id && json.customer_name !== '未設定';
});

// 顧客選択肢を作成（gcp_service_account_jsonは選択顧客データ解析で追加）
const clientOptions = validClients.map(item => {
  return {
    name: item.json.customer_name,
    value: JSON.stringify({
      customer_name: item.json.customer_name,
      drive_folder_id: item.json.drive_folder_id,
      sheet_id: item.json.sheet_id,
      line_user_id: item.json.line_user_id
    })
  };
});

return {
  json: {
    clients: clientOptions
  }
};

// ここでは手動実行の簡略版として1番目の顧客を選択
// 実際の運用では、n8nのForm TriggerまたはWebhook Triggerで顧客選択UIを実装
const clientsData = $('顧客リスト作成').item.json.clients;
if (!clientsData || clientsData.length === 0) {
  throw new Error('設定済みの顧客が見つかりませんでした');
}

// 最初の顧客を選択（デモ用）
const selectedClientData = JSON.parse(clientsData[0].value);

// GCP認証情報を取得（全顧客共通）
// Master_User_Configから全データを取得してamex_testのGCP認証情報を探す
const allClients = $('全クライアント取得').all();
const amexTestClient = allClients.find(c => c.json.customer_name === 'amex_test');

if (!amexTestClient || !amexTestClient.json.gcp_service_account_json) {
  throw new Error('GCP認証情報が見つかりません。Master_User_Configのamex_test行にgcp_service_account_jsonを設定してください。');
}

// 選択顧客データにGCP認証情報を追加
selectedClientData.gcp_service_account_json = amexTestClient.json.gcp_service_account_json;

return {
  json: selectedClientData
};

// Google Drive APIのレスポンスからfiles配列を展開
const files = $json.files || [];

if (files.length === 0) {
  throw new Error('未処理の画像ファイルが見つかりませんでした');
}

// 顧客情報を保持
const driveFolder = $('未処理画像リスト取得').first().json.drive_folder_id;

console.log(`画像リスト展開: ${files.length}件の画像を検出`);

// 【デバッグモード】最初の1枚のみ処理
const DEBUG_MODE = false;  // 全画像を処理
const filesToProcess = DEBUG_MODE ? files.slice(0, 1) : files;

if (DEBUG_MODE) {
  console.log(`⚠️ デバッグモード: 最初の1枚のみ処理します`);
  console.log(`処理対象: ${filesToProcess[0].name}`);
} else {
  console.log(`✅ 全${files.length}件の画像を処理します`);
}

// 各ファイルを個別のアイテムとして返す
return filesToProcess.map(file => ({
  json: {
    id: file.id,
    name: file.name,
    mimeType: file.mimeType,
    drive_folder_id: driveFolder
  }
}));

// Google Cloud Vision APIでOCR実行
const binaryPropertyName = Object.keys($input.item.binary)[0];
const binaryBuffer = await this.helpers.getBinaryDataBuffer(0, binaryPropertyName);
const base64String = binaryBuffer.toString('base64');

const pdfFileName = $('画像ダウンロード').item.json.name;
const pdfFileId = $('画像ダウンロード').item.json.id;

console.log(`\n=== Google Cloud Vision OCR: ${pdfFileName} ===`);
console.log(`画像サイズ: ${binaryBuffer.length} bytes`);
console.log(`Base64長: ${base64String.length} 文字`);

// Vision APIリクエストボディ
const visionRequest = {
  requests: [
    {
      image: {
        content: base64String
      },
      features: [
        {
          type: "DOCUMENT_TEXT_DETECTION"
        }
      ],
      imageContext: {
        languageHints: ["ja"]
      }
    }
  ]
};

// 顧客データを取得
const customerData = $('選択顧客データ解析').item.json;

return {
  json: {
    visionRequest: visionRequest,
    pdf_file_name: pdfFileName,
    pdf_file_id: pdfFileId,
    customer_name: customerData.customer_name,
    sheet_id: customerData.sheet_id,
    drive_folder_id: customerData.drive_folder_id,
    gcp_service_account_json: customerData.gcp_service_account_json
  }
};

// Google Cloud Vision APIをサービスアカウントで認証して呼び出し
const visionRequest = $json.visionRequest;
const pdfFileName = $json.pdf_file_name;
const pdfFileId = $json.pdf_file_id;
const customerName = $json.customer_name;
const sheetId = $json.sheet_id;
const driveFolderId = $json.drive_folder_id;
const gcpServiceAccountJson = $json.gcp_service_account_json;

console.log(`\n=== Vision API呼び出し: ${pdfFileName} ===`);
console.log('受信データ:', JSON.stringify($json, null, 2).substring(0, 500));

if (!gcpServiceAccountJson) {
  throw new Error(`gcp_service_account_jsonが設定されていません。Master_User_Configシートで設定してください。`);
}

// GCPサービスアカウント情報を取得
const serviceAccount = JSON.parse(gcpServiceAccountJson);

// JWT作成（簡易版 - Google Cloud Vision API用）
const crypto = require('crypto');

const now = Math.floor(Date.now() / 1000);
const jwtHeader = Buffer.from(JSON.stringify({
  alg: 'RS256',
  typ: 'JWT'
})).toString('base64url');

const jwtClaim = Buffer.from(JSON.stringify({
  iss: serviceAccount.client_email,
  scope: 'https://www.googleapis.com/auth/cloud-vision',
  aud: 'https://oauth2.googleapis.com/token',
  exp: now + 3600,
  iat: now
})).toString('base64url');

const signatureInput = `${jwtHeader}.${jwtClaim}`;
const signature = crypto.createSign('RSA-SHA256')
  .update(signatureInput)
  .sign(serviceAccount.private_key, 'base64url');

const jwt = `${signatureInput}.${signature}`;

console.log('JWT生成完了');

// アクセストークン取得（this.helpers.httpRequestを使用）
const tokenResponse = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://oauth2.googleapis.com/token',
  body: {
    grant_type: 'urn:ietf:params:oauth:grant-type:jwt-bearer',
    assertion: jwt
  },
  json: true
});

const tokenData = tokenResponse;
const accessToken = tokenData.access_token;

if (!accessToken) {
  throw new Error(`トークン取得失敗: ${JSON.stringify(tokenData)}`);
}

console.log('アクセストークン取得成功');

// Vision API呼び出し（this.helpers.httpRequestを使用）
const visionResponse = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://vision.googleapis.com/v1/images:annotate',
  headers: {
    Authorization: `Bearer ${accessToken}`
  },
  body: visionRequest,
  json: true
});

const visionData = visionResponse;

console.log('✓ Vision API成功');

// Vision APIレスポンスと元のデータを両方返す
return {
  json: {
    visionResponse: visionData,
    pdf_file_name: pdfFileName,
    pdf_file_id: pdfFileId,
    customer_name: customerName,
    sheet_id: sheetId,
    drive_folder_id: driveFolderId
  }
};

// Google Cloud Vision OCRの結果からテキストを抽出
const visionResponse = $json.visionResponse;
const pdfFileName = $json.pdf_file_name;
const pdfFileId = $json.pdf_file_id;
const customerName = $json.customer_name;
const sheetId = $json.sheet_id;
const driveFolderId = $json.drive_folder_id;

console.log(`\n=== OCRテキスト抽出: ${pdfFileName} ===`);

// Vision APIレスポンスからテキストを取得
let extractedText = '';

if (visionResponse.responses && visionResponse.responses.length > 0) {
  const response = visionResponse.responses[0];

  if (response.fullTextAnnotation && response.fullTextAnnotation.text) {
    extractedText = response.fullTextAnnotation.text;
    console.log(`✓ テキスト抽出成功: ${extractedText.length} 文字`);
    console.log(`先頭200文字: ${extractedText.substring(0, 200)}...`);
  } else if (response.error) {
    throw new Error(`Vision API エラー: ${JSON.stringify(response.error)}`);
  } else {
    throw new Error(`テキストが検出されませんでした`);
  }
} else {
  throw new Error(`Vision APIレスポンスが空です`);
}

if (extractedText.length === 0) {
  throw new Error(`OCRで抽出されたテキストが空です`);
}

return {
  json: {
    extracted_text: extractedText,
    pdf_file_name: pdfFileName,
    pdf_file_id: pdfFileId,
    customer_name: customerName,
    sheet_id: sheetId,
    drive_folder_id: driveFolderId
  }
};

