parameters.jsCode:
// 全アイテムを取得（各画像のGPT-4oレスポンス）
const items = $input.all();
const allTransactions = [];
const errors = [];

console.log(`=== 取引データ展開ノード開始: ${items.length}件の画像レスポンスを処理 ===`);

// OCRテキスト抽出ノードから画像ファイル情報と顧客データを取得
const ocrTextItems = $('OCRテキスト抽出').all();

// 最初のアイテムから顧客データを取得（全ての画像で同じ）
const firstOcrItem = ocrTextItems[0]?.json;
const customer_name = firstOcrItem?.customer_name;
const sheet_id = firstOcrItem?.sheet_id;
const drive_folder_id = firstOcrItem?.drive_folder_id;

console.log('顧客データ:', { customer_name, sheet_id, drive_folder_id });

// 各画像のGPT-4oレスポンスを処理
for (let i = 0; i < items.length; i++) {
  const item = items[i];

  // 対応する画像ファイル情報を取得
  const pdf_file_name = ocrTextItems[i]?.json?.pdf_file_name || `unknown_${i}.png`;
  const pdf_file_id = ocrTextItems[i]?.json?.pdf_file_id || `unknown_id_${i}`;

  console.log(`\n[${i+1}/${items.length}] 処理中: ${pdf_file_name}`);

  // GPT-4oのレスポンスを取得
  let content;
  try {
    content = item.json.choices[0].message.content;
  } catch (error) {
    const errorMsg = `レスポンス構造エラー [${pdf_file_name}]: ${error.message}`;
    console.error(errorMsg);
    console.error('item.json:', JSON.stringify(item.json, null, 2));
    errors.push(errorMsg);
    continue;
  }

  // マークダウンコードブロックを除去
  content = content.replace(/^```json\s*\n?/, '').replace(/\n?```\s*$/, '').trim();

  console.log(`レスポンス (最初の200文字): ${content.substring(0, 200)}...`);

  // ★ JSONガード: { または [ で始まっていない場合はスキップ
  if (!content.startsWith('{') && !content.startsWith('[')) {
    const errorMsg = `JSON形式ではないレスポンス [${pdf_file_name}]: ${content.substring(0, 100)}...`;
    console.warn(errorMsg);
    errors.push(errorMsg);
    continue;
  }

  // JSON.parseをtry/catchでラップ
  let structuredData;
  try {
    structuredData = JSON.parse(content);
  } catch (error) {
    const errorMsg = `JSON解析エラー [${pdf_file_name}]: ${error.message}`;
    console.error(errorMsg);
    console.error(`元のレスポンス全文:\n${content}`);
    errors.push(errorMsg);
    continue;
  }

  // transactions配列を取得
  const transactions = structuredData.transactions || [];

  if (transactions.length === 0) {
    const warnMsg = `取引データなし [${pdf_file_name}]`;
    console.warn(warnMsg);
    console.warn(`structuredData: ${JSON.stringify(structuredData, null, 2)}`);
    errors.push(warnMsg);
    continue;
  }

  console.log(`✓ ${transactions.length}件の取引を抽出`);

  // 各取引に共通データを付与して配列に追加
  for (const transaction of transactions) {
    allTransactions.push({
      json: {
        ...transaction,
        pdf_file_name,
        pdf_file_id,
        customer_name,
        sheet_id,
        drive_folder_id
      }
    });
  }
}

console.log(`\n=== 処理結果 ===`);
console.log(`成功: ${allTransactions.length}件の取引データを抽出`);
console.log(`エラー/警告: ${errors.length}件`);
if (errors.length > 0) {
  console.log('エラー詳細:', errors.join('\n'));
}

// ★ マイルドな終了: 取引が1件も抽出できなかった場合は空配列を返す
if (allTransactions.length === 0) {
  console.warn('⚠️ 全ての画像から取引データを抽出できませんでした');
  console.warn(`処理した画像数: ${items.length}件`);
  console.warn(`エラー詳細: ${errors.join('; ')}`);

  // 完全に空配列を返す（後続のノードは実行されない）
  return [];
}

console.log('最初の取引サンプル:', JSON.stringify(allTransactions[0].json, null, 2));

return allTransactions;

// 警告レコード（取引データなし）はスキップ
if ($json.warning) {
  console.warn('勘定科目判定: スキップ（警告レコード）');
  return [];  // このアイテムは次のノードに渡さない
}

// 取引データを前のノードから取得
const transactionData = $json;

// 一時的にナレッジベースをスキップし、常にAI推論を使用
return {
  json: {
    ...transactionData,
    matched_category: null,
    match_source: 'ai_inference_needed',
    matched_confidence: 0
  }
};

// 警告レコード（AI推論対象外）はスキップ
if ($json.warning || !$json.choices) {
  console.warn('AI推論結果マージ: スキップ（警告レコードまたはAI推論対象外）');
  return [];  // このアイテムは次のノードに渡さない
}

// AI推論結果をパース
let content = $json.choices[0].message.content;

// JSONブロックを抽出（```json...```の部分のみ）
const jsonMatch = content.match(/```json\s*\n?([\s\S]*?)\n?```/);
if (jsonMatch) {
  content = jsonMatch[1].trim();
} else {
  // マークダウンブロックがない場合は、{...}を探す
  const objectMatch = content.match(/\{[\s\S]*\}/);
  if (objectMatch) {
    content = objectMatch[0];
  } else {
    content = content.trim();
  }
}

let aiResult;
try {
  aiResult = JSON.parse(content);
} catch (error) {
  throw new Error(`JSON解析エラー: ${error.message}\n元のレスポンス: ${content}`);
}

// 元のデータとマージ
const originalData = $('IF AI推論が必要').item.json;

return {
  json: {
    ...originalData,
    matched_category: aiResult.account_category,
    match_source: 'ai_inference'
  }
};

// 警告レコード（取引データなし）はスキップ
if ($json.warning) {
  console.warn('消費税率判定: スキップ（警告レコード）');
  return [];  // このアイテムは次のノードに渡さない
}

// 消費税率の自動判定
const merchant = $json.merchant || '';
let taxRate = 10; // デフォルトは標準税率10%

// 軽減税率8%対象店舗
const reducedTaxMerchants = [
  'セブン', 'ローソン', 'ファミマ', 'ファミリーマート',
  'イオン', '西友', 'マックスバリュ',
  'Uber Eats', '出前館', 'menu', 'Wolt'
];

for (const keyword of reducedTaxMerchants) {
  if (merchant.includes(keyword)) {
    taxRate = 8;
    break;
  }
}

// 税区分判定（アメックスはインボイス登録事業者なのでインボイス対応）
const taxType = taxRate === 10 ? '課10% インボイス' : '課8% インボイス';

return {
  json: {
    ...$json,
    tax_rate: taxRate,
    tax_type: taxType,
    // 貸方勘定科目は法人カードなので「未払金」
    credit_account: '未払金'
  }
};

// 消費税率判定ノードから画像ファイル情報を取得
// （仕訳台帳へ記帳ノードはGoogle Sheetsの出力を返すため、元のデータは失われる）
const taxRateData = $('消費税率判定').item.json;
const imageFileId = taxRateData.pdf_file_id;
const imageFileName = taxRateData.pdf_file_name;

// 画像情報がなければ、このアイテムはスキップ（警告レコードなど）
if (!imageFileId || !imageFileName) {
  console.warn('リネーム対象外アイテム（画像情報なし）: ', JSON.stringify(taxRateData, null, 2));
  return [];  // このアイテムは次のノードに渡さない
}

// 新しいファイル名を生成（_processed を追加）
const newFileName = imageFileName.replace(/\.(png|jpg|jpeg)$/i, '_processed.$1');

return {
  json: {
    file_id: imageFileId,
    new_name: newFileName,
    old_name: imageFileName
  }
};
