// Master_User_Configから顧客リストを作成
const clients = $input.all();

// drive_folder_idとsheet_idが両方設定されている顧客のみ抽出
const validClients = clients.filter(item => {
  const json = item.json;
  return json.drive_folder_id && json.sheet_id && json.customer_name !== '未設定';
});

// 顧客選択肢を作成
const clientOptions = validClients.map(item => {
  return {
    name: item.json.customer_name,
    value: JSON.stringify({
      customer_name: item.json.customer_name,
      drive_folder_id: item.json.drive_folder_id,
      sheet_id: item.json.sheet_id,
      line_user_id: item.json.line_user_id
    })
  };
});

return {
  json: {
    clients: clientOptions
  }
};

// ここでは手動実行の簡略版として1番目の顧客を選択
// 実際の運用では、n8nのForm TriggerまたはWebhook Triggerで顧客選択UIを実装
const clientsData = $('顧客リスト作成').item.json.clients;
if (!clientsData || clientsData.length === 0) {
  throw new Error('設定済みの顧客が見つかりませんでした');
}

// 最初の顧客を選択（デモ用）
const selectedClientData = JSON.parse(clientsData[0].value);

return {
  json: selectedClientData
};

// PDFバイナリデータをBase64エンコード
const binaryPropertyName = Object.keys($input.item.binary)[0];
const mimeType = $input.item.binary[binaryPropertyName].mimeType || 'application/pdf';

const binaryBuffer = await this.helpers.getBinaryDataBuffer(0, binaryPropertyName);
const base64String = binaryBuffer.toString('base64');

const base64Pdf = `data:${mimeType};base64,${base64String}`;

// OpenAI APIリクエストボディを構築
const requestBody = {
  model: "gpt-4o",
  messages: [
    {
      role: "system",
      content: `あなたはアメックス明細PDFを解析する経理の専門家AIです。PDFから全ての取引データを抽出してください。

# ルール
1. **複数の取引**を1つのJSON配列として抽出してください。
2. **各取引**について以下を抽出:
   - **transaction_date**: 取引日（YYYY-MM-DD形式）
   - **merchant**: 支払先（店舗名・会社名）
   - **amount**: 金額（数値のみ、カンマや円マークは除去）
   - **description**: 取引内容（明細に記載があれば）

# 出力形式
JSONのみを出力してください。説明文は不要です。

{
  "transactions": [
    {
      "transaction_date": "2025-01-15",
      "merchant": "Amazon.co.jp",
      "amount": 5280,
      "description": "書籍購入"
    },
    {
      "transaction_date": "2025-01-16",
      "merchant": "セブンイレブン",
      "amount": 850,
      "description": ""
    }
  ]
}`
    },
    {
      role: "user",
      content: [
        {
          type: "text",
          text: "このアメックス明細PDFから全ての取引データを抽出してください。"
        },
        {
          type: "image_url",
          image_url: {
            url: base64Pdf
          }
        }
      ]
    }
  ],
  max_tokens: 2000,
  temperature: 0.1
};

// PDFファイル名も保持
const pdfFileName = $('PDFダウンロード').item.json.name;
const pdfFileId = $('PDFダウンロード').item.json.id;

return {
  json: {
    requestBody: requestBody,
    pdf_file_name: pdfFileName,
    pdf_file_id: pdfFileId,
    customer_name: $('選択顧客データ解析').item.json.customer_name,
    sheet_id: $('選択顧客データ解析').item.json.sheet_id,
    drive_folder_id: $('選択顧客データ解析').item.json.drive_folder_id
  }
};

// GPT-4oのレスポンスをパース
let content = $json.choices[0].message.content;
content = content.replace(/^```json\s*\n?/, '').replace(/\n?```\s*$/, '').trim();
const ocrResult = JSON.parse(content);

// transactions配列を個別のアイテムに展開
const transactions = ocrResult.transactions || [];

if (transactions.length === 0) {
  throw new Error('PDFから取引データを抽出できませんでした');
}

// 各取引に共通データを付与
const pdf_file_name = $('PDF OCRリクエスト構築').item.json.pdf_file_name;
const pdf_file_id = $('PDF OCRリクエスト構築').item.json.pdf_file_id;
const customer_name = $('PDF OCRリクエスト構築').item.json.customer_name;
const sheet_id = $('PDF OCRリクエスト構築').item.json.sheet_id;
const drive_folder_id = $('PDF OCRリクエスト構築').item.json.drive_folder_id;

return transactions.map(transaction => ({
  json: {
    ...transaction,
    pdf_file_name,
    pdf_file_id,
    customer_name,
    sheet_id,
    drive_folder_id
  }
}));

// 取引データを取得
const transactionData = $('取引データ展開').item.json;
const merchant = transactionData.merchant || '';
const description = transactionData.description || '';

// ナレッジベース取得
let knowledgeBase = [];
try {
  const kbData = $('勘定科目マスター取得').all();
  if (kbData && Array.isArray(kbData) && kbData.length > 0) {
    knowledgeBase = kbData.filter(item => item && item.json && !item.error);
  }
} catch (error) {
  knowledgeBase = [];
}

// マッチング処理
let matchedCategory = null;
let maxConfidence = 0;
let matchedRowId = null;
let matchedMerchantKeyword = null;
let matchedAccountCategory = null;
let matchedUsageCount = 0;
let matchedLastUsedDate = '';
let matchedConfidenceScore = 0;

for (let i = 0; i < knowledgeBase.length; i++) {
  const rule = knowledgeBase[i].json;
  const merchantKeyword = rule.merchant_keyword || '';
  const descriptionKeyword = rule.description_keyword || '';
  const confidence = parseInt(rule.confidence_score) || 0;
  const rowNumber = i + 2;

  // 店舗名での完全一致（最優先）
  if (merchantKeyword && merchant.includes(merchantKeyword)) {
    if (confidence > maxConfidence) {
      matchedCategory = rule.account_category;
      maxConfidence = confidence;
      matchedRowId = rowNumber;
      matchedMerchantKeyword = merchantKeyword;
      matchedAccountCategory = rule.account_category;
      matchedUsageCount = rule.usage_count || 0;
      matchedLastUsedDate = rule.last_used_date || '';
      matchedConfidenceScore = confidence;
    }
  }

  // 取引内容での部分一致
  if (!matchedCategory && descriptionKeyword && description.includes(descriptionKeyword)) {
    if (confidence > maxConfidence) {
      matchedCategory = rule.account_category;
      maxConfidence = confidence;
      matchedRowId = rowNumber;
      matchedMerchantKeyword = merchantKeyword;
      matchedAccountCategory = rule.account_category;
      matchedUsageCount = rule.usage_count || 0;
      matchedLastUsedDate = rule.last_used_date || '';
      matchedConfidenceScore = confidence;
    }
  }
}

// マッチしない場合はAI推定を呼び出す準備
return {
  json: {
    ...transactionData,
    matched_category: matchedCategory,
    match_source: matchedCategory ? 'knowledge_base' : 'ai_inference_needed',
    matched_confidence: maxConfidence,
    matched_row_id: matchedRowId,
    merchant_keyword: matchedMerchantKeyword,
    account_category: matchedAccountCategory,
    usage_count: matchedUsageCount,
    last_used_date: matchedLastUsedDate,
    confidence_score: matchedConfidenceScore
  }
};

