// AI推論結果をパース
let content = $json.choices[0].message.content;
content = content.replace(/^```json\s*\n?/, '').replace(/\n?```\s*$/, '').trim();
const aiResult = JSON.parse(content);

// 元のデータとマージ
const originalData = $('IF AI推論が必要').item.json;

return {
  json: {
    ...originalData,
    matched_category: aiResult.account_category,
    match_source: 'ai_inference'
  }
};

// 消費税率の自動判定
const merchant = $json.merchant || '';
let taxRate = 10; // デフォルトは標準税率10%

// 軽減税率8%対象店舗
const reducedTaxMerchants = [
  'セブン', 'ローソン', 'ファミマ', 'ファミリーマート',
  'イオン', '西友', 'マックスバリュ',
  'Uber Eats', '出前館', 'menu', 'Wolt'
];

for (const keyword of reducedTaxMerchants) {
  if (merchant.includes(keyword)) {
    taxRate = 8;
    break;
  }
}

// 税区分判定（アメックスはインボイス登録事業者なのでインボイス対応）
const taxType = taxRate === 10 ? '課10% インボイス' : '課8% インボイス';

return {
  json: {
    ...$json,
    tax_rate: taxRate,
    tax_type: taxType,
    // 貸方勘定科目は法人カードなので「未払金」
    credit_account: '未払金'
  }
};

// 全取引の記帳が完了したら、PDFファイルを処理済みマーキング
const pdfFileId = $('PDF OCRリクエスト構築').item.json.pdf_file_id;
const pdfFileName = $('PDF OCRリクエスト構築').item.json.pdf_file_name;

// 新しいファイル名を生成（_processed を追加）
const newFileName = pdfFileName.replace(/\.pdf$/i, '_processed.pdf');

return {
  json: {
    file_id: pdfFileId,
    new_name: newFileName,
    old_name: pdfFileName
  }
};
