// Google Drive Downloadノードからバイナリデータを取得
const binaryPropertyName = Object.keys($input.item.binary)[0];
const binaryBuffer = await this.helpers.getBinaryDataBuffer($itemIndex, binaryPropertyName);
const base64String = binaryBuffer.toString('base64');

const driveFileId = $json.id;
const driveFileName = $json.name;

console.log(`\n=== Google Cloud Vision OCR準備: ${driveFileName} ===`);
console.log(`画像サイズ: ${binaryBuffer.length} bytes`);
console.log(`Base64長: ${base64String.length} 文字`);

// 顧客情報を取得（Amex設定済み顧客フィルタノードから）
const clientData = $('Amex設定済み顧客フィルタ').item.json;

// GCP認証情報を取得（全顧客取得ノードから）
const allClients = $('全顧客取得').all();
const amexTestClient = allClients.find(c => c.json.customer_name === 'amex_test');

if (!amexTestClient || !amexTestClient.json.gcp_service_account_json) {
  throw new Error('GCP認証情報が見つかりません。Master_User_Configのamex_test行にgcp_service_account_jsonを設定してください。');
}

// Vision APIリクエストボディ
const visionRequest = {
  requests: [
    {
      image: {
        content: base64String
      },
      features: [
        {
          type: "DOCUMENT_TEXT_DETECTION"
        }
      ],
      imageContext: {
        languageHints: ["ja"]
      }
    }
  ]
};

return {
  json: {
    visionRequest: visionRequest,
    driveFileId: driveFileId,
    driveFileName: driveFileName,
    client_code: clientData.client_code,
    amex_sheet_id: clientData.amex_sheet_id,
    gcp_service_account_json: amexTestClient.json.gcp_service_account_json
  }
};

// Google Cloud Vision APIをサービスアカウントで認証して呼び出し
const visionRequest = $json.visionRequest;
const driveFileId = $json.driveFileId;
const driveFileName = $json.driveFileName;
const clientCode = $json.client_code;
const amexSheetId = $json.amex_sheet_id;
const gcpServiceAccountJson = $json.gcp_service_account_json;

if (!gcpServiceAccountJson) {
  throw new Error('gcp_service_account_jsonが設定されていません。Master_User_Configシートで設定してください。');
}

// GCPサービスアカウント情報を取得
const serviceAccount = JSON.parse(gcpServiceAccountJson);

// JWT作成（Google Cloud Vision API用）
const crypto = require('crypto');

const now = Math.floor(Date.now() / 1000);
const jwtHeader = Buffer.from(JSON.stringify({
  alg: 'RS256',
  typ: 'JWT'
})).toString('base64url');

const jwtClaim = Buffer.from(JSON.stringify({
  iss: serviceAccount.client_email,
  scope: 'https://www.googleapis.com/auth/cloud-vision',
  aud: 'https://oauth2.googleapis.com/token',
  exp: now + 3600,
  iat: now
})).toString('base64url');

const signatureInput = `${jwtHeader}.${jwtClaim}`;
const signature = crypto.createSign('RSA-SHA256')
  .update(signatureInput)
  .sign(serviceAccount.private_key, 'base64url');

const jwt = `${signatureInput}.${signature}`;

// アクセストークン取得
const tokenResponse = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://oauth2.googleapis.com/token',
  body: {
    grant_type: 'urn:ietf:params:oauth:grant-type:jwt-bearer',
    assertion: jwt
  },
  json: true
});

const accessToken = tokenResponse.access_token;

if (!accessToken) {
  throw new Error(`トークン取得失敗: ${JSON.stringify(tokenResponse)}`);
}

// Vision API呼び出し
const visionResponse = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://vision.googleapis.com/v1/images:annotate',
  headers: {
    Authorization: `Bearer ${accessToken}`
  },
  body: visionRequest,
  json: true
});

return {
  json: {
    visionResponse: visionResponse,
    driveFileId: driveFileId,
    driveFileName: driveFileName,
    client_code: clientCode,
    amex_sheet_id: amexSheetId
  }
};

// Google Cloud Vision OCRの結果からテキストを抽出
const visionResponse = $json.visionResponse;
const driveFileId = $json.driveFileId;
const driveFileName = $json.driveFileName;
const clientCode = $json.client_code;
const amexSheetId = $json.amex_sheet_id;

console.log(`\n=== OCRテキスト抽出: ${driveFileName} ===`);

// Vision APIレスポンスからテキストを取得
let extractedText = '';

if (visionResponse.responses && visionResponse.responses.length > 0) {
  const response = visionResponse.responses[0];

  if (response.fullTextAnnotation && response.fullTextAnnotation.text) {
    extractedText = response.fullTextAnnotation.text;
    console.log(`✓ テキスト抽出成功: ${extractedText.length}文字`);
    console.log('\n--- OCRテキスト全文 ---');
    console.log(extractedText);
    console.log('--- OCRテキスト終了 ---\n');
  } else if (response.error) {
    throw new Error(`Vision API エラー: ${JSON.stringify(response.error)}`);
  } else {
    throw new Error('テキストが検出されませんでした');
  }
} else {
  throw new Error('Vision APIレスポンスが空です');
}

if (extractedText.length === 0) {
  throw new Error('OCRで抽出されたテキストが空です');
}

return {
  json: {
    extracted_text: extractedText,
    driveFileId: driveFileId,
    driveFileName: driveFileName,
    client_code: clientCode,
    amex_sheet_id: amexSheetId
  }
};

