parameters.jsCode:
// すべてのGPT-4oレスポンスを取得（Run Once for All Items モード）
const gptItems = $input.all();
const ocrItems = $('OCRテキスト抽出').all();

console.log(`\n=== 取引データ展開開始 ===`);
console.log(`GPT-4oアイテム数: ${gptItems.length}`);
console.log(`OCRアイテム数: ${ocrItems.length}`);

const allTransactions = [];

// 各GPT-4oレスポンスを処理
for (let i = 0; i < gptItems.length; i++) {
  const gptResponse = gptItems[i].json;
  const ocrData = ocrItems[i].json;

  console.log(`\n--- 画像 ${i + 1}/${gptItems.length}: ${ocrData.driveFileName} ---`);

  if (!gptResponse.choices || !gptResponse.choices[0]) {
    console.warn(`⚠️  GPT-4oレスポンスが不正です（画像 ${i + 1}）。スキップします。`);
    continue;
  }

  let content = gptResponse.choices[0].message.content;
  content = content.replace(/^```json\s*\n?/, '').replace(/\n?```\s*$/, '').trim();

  console.log(`GPT-4oレスポンス（最初の500文字）: ${content.substring(0, 500)}`);

  // JSONガード
  if (!content.startsWith('{') && !content.startsWith('[')) {
    console.warn(`⚠️  JSON形式ではないレスポンス（画像 ${i + 1}）。スキップします。`);
    console.warn(`レスポンス: ${content.substring(0, 200)}...`);
    continue;
  }

  let structuredData;
  try {
    structuredData = JSON.parse(content);
  } catch (error) {
    console.warn(`⚠️  JSON解析エラー（画像 ${i + 1}）: ${error.message}。スキップします。`);
    console.warn(`レスポンス: ${content}`);
    continue;
  }

  const transactions = structuredData.transactions || [];
  console.log(`取引データ数: ${transactions.length}件`);

  if (transactions.length === 0) {
    console.warn(`⚠️  取引データが見つかりませんでした（画像 ${i + 1}）。スキップします。`);
    continue;
  }

  // 各取引にメタデータを追加
  for (let j = 0; j < transactions.length; j++) {
    const transaction = transactions[j];
    console.log(`  取引 ${j + 1}: ${transaction.merchant} - ${transaction.amount}円`);

    allTransactions.push({
      json: {
        transaction_date: transaction.transaction_date,
        merchant: transaction.merchant,
        amount: transaction.amount,
        description: transaction.description || '',
        category: transaction.category || '',
        driveUrl: `https://drive.google.com/uc?export=view&id=${ocrData.driveFileId}`,
        driveFileId: ocrData.driveFileId,
        driveFileName: ocrData.driveFileName,
        client_code: ocrData.client_code,
        amex_sheet_id: ocrData.amex_sheet_id
      }
    });
  }
}

console.log(`\n=== 取引データ展開完了 ===`);
console.log(`総取引数: ${allTransactions.length}件`);

if (allTransactions.length === 0) {
  console.warn(`⚠️  処理可能な取引データがありませんでした。`);
  return [];
}

return allTransactions;

// 取引データを現在の入力から取得
const transaction = $input.item.json;
const merchant = transaction.merchant || '';
const description = transaction.description || '';

console.log(`\n=== 勘定科目マッチング: ${merchant} ===`);

// ナレッジベース取得（全行）- エラーハンドリング追加
let knowledgeBase = [];
try {
  const kbData = $('勘定科目マスター取得').all();
  if (kbData && Array.isArray(kbData) && kbData.length > 0) {
    knowledgeBase = kbData.filter(item => item && item.json && !item.error && !item.json.error);
  }
} catch (error) {
  console.log('ナレッジベース取得エラー（スキップしてAI推論に回します）:', error.message);
  knowledgeBase = [];
}

console.log(`ナレッジベース: ${knowledgeBase.length}件`);

// マッチング処理
let matchedCategory = null;
let maxConfidence = 0;
let matchSource = 'none';

for (let i = 0; i < knowledgeBase.length; i++) {
  const rule = knowledgeBase[i].json;
  const merchantKeyword = rule.merchant_keyword || '';
  const descriptionKeyword = rule.description_keyword || '';
  const confidence = parseInt(rule.confidence_score) || 0;

  // 店舗名での完全一致（最優先）
  if (merchantKeyword && merchant.includes(merchantKeyword)) {
    if (confidence > maxConfidence) {
      matchedCategory = rule.account_category;
      maxConfidence = confidence;
      matchSource = 'knowledge_base';
    }
  }

  // 取引内容での部分一致
  if (!matchedCategory && descriptionKeyword && description.includes(descriptionKeyword)) {
    if (confidence > maxConfidence) {
      matchedCategory = rule.account_category;
      maxConfidence = confidence;
      matchSource = 'knowledge_base';
    }
  }
}

// ナレッジベースでマッチしなかった場合（またはナレッジベースがない場合）はAI推論が必要
if (!matchedCategory) {
  console.log(`${merchant}: ナレッジベースマッチなし → AI推論`);
  return {
    json: {
      ...transaction,
      needs_ai_inference: true,
      match_source: 'pending'
    }
  };
} else {
  console.log(`${merchant}: ナレッジベースマッチ成功 → ${matchedCategory}`);
  return {
    json: {
      ...transaction,
      category: matchedCategory,
      match_source: matchSource,
      matched_confidence: maxConfidence,
      needs_ai_inference: false
    }
  };
}

// 取引情報を取得
const transaction = $input.item.json;

// AI推論用プロンプト構築
const merchant = transaction.merchant || '不明';
const description = transaction.description || '詳細不明';
const amount = transaction.amount || 0;

const prompt = `以下の取引から適切な勘定科目を推定してください。

店舗名: ${merchant}
取引内容: ${description}
金額: ${amount}円

勘定科目の選択肢:
- 消耗品費
- 旅費交通費
- 会議費
- 交際費
- 通信費
- 水道光熱費
- 広告宣伝費
- 研修費
- 新聞図書費
- 車両費
- 雑費

JSON形式で出力してください:
{
  "category": "推定した勘定科目",
  "tax_rate": 10
}`;

const requestBody = {
  model: "gpt-4o-mini",
  messages: [
    {
      role: "system",
      content: "あなたは経理の専門家AIです。取引内容から適切な勘定科目を推定してください。出力は必ずJSON形式のみとし、説明文は一切含めないでください。"
    },
    {
      role: "user",
      content: prompt
    }
  ],
  max_tokens: 200,
  temperature: 0
};

return {
  json: {
    requestBody: requestBody,
    transaction: transaction
  }
};

// AI推論結果をパース
let content = $input.item.json.choices[0].message.content;

// マークダウンコードブロックを除去
content = content.replace(/^```json\s*\n?/, '').replace(/\n?```\s*$/, '').trim();

// 説明文を除去（JSONの前にある説明文を削除）
const jsonMatch = content.match(/\{[\s\S]*\}/);
if (jsonMatch) {
  content = jsonMatch[0];
}

let aiResult;
try {
  aiResult = JSON.parse(content);
} catch (error) {
  throw new Error(`JSON解析エラー: ${error.message}\n元のレスポンス: ${content}`);
}

// AI推論リクエスト構築ノードから元の取引データを取得
const transaction = $('AI推論リクエスト構築').item.json.transaction;

return {
  json: {
    ...transaction,
    category: aiResult.category,
    tax_rate: aiResult.tax_rate || 10,
    match_source: 'ai_inference',
    matched_confidence: 0,
    needs_ai_inference: false
  }
};

// 勘定科目判定結果を取得
const judgmentResult = $input.item.json;

// 消費税率の判定（軽減税率対応）
let taxRate = 10; // デフォルトは標準税率
const merchant = judgmentResult.merchant || '';

// 軽減税率（8%）対象店舗
const reducedTaxMerchants = [
  'セブン', 'ローソン', 'ファミマ', 'ファミリーマート',
  'イオン', '西友', 'UBER EATS', 'Uber Eats', '出前館'
];

for (const keyword of reducedTaxMerchants) {
  if (merchant.includes(keyword)) {
    taxRate = 8;
    break;
  }
}

// 税区分の判定
const taxType = taxRate === 10 ? "課10%" : "課8%";

return {
  json: {
    ...judgmentResult,
    tax_rate: taxRate,
    tax_type: taxType
  }
};

// 全取引の記帳完了後、処理済みマーキング用のデータを準備
// OCRテキスト抽出ノードから全画像を取得（取引の有無に関わらず）

console.log('\n=== リネーム準備 ===');

// OCRテキスト抽出ノードから全画像を取得
const ocrItems = $('OCRテキスト抽出').all();

if (!ocrItems || ocrItems.length === 0) {
  throw new Error('OCRテキスト抽出ノードからデータを取得できませんでした');
}

console.log(`OCR処理済み画像数: ${ocrItems.length}件`);

// driveFileIdでグループ化してユニークなファイルリストを作成
const fileMap = new Map();

for (const item of ocrItems) {
  const driveFileId = item.json.driveFileId;
  const driveFileName = item.json.driveFileName;

  if (driveFileId && driveFileName && !fileMap.has(driveFileId)) {
    fileMap.set(driveFileId, driveFileName);
  }
}

console.log(`ユニークなファイル数: ${fileMap.size}件`);

// 各ファイルを個別のアイテムとして返す
const results = [];
for (const [driveFileId, driveFileName] of fileMap.entries()) {
  console.log(`  - ${driveFileName} (ID: ${driveFileId.substring(0, 20)}...)`);
  results.push({
    json: {
      driveFileId: driveFileId,
      driveFileName: driveFileName
    }
  });
}

console.log(`\nリネーム対象ファイル数: ${results.length}件`);
console.log('取引の有無に関わらず、OCR処理が完了した全ファイルをリネームします。\n');

return results;
