// Google Drive Downloadノードからバイナリデータを取得
const binaryPropertyName = Object.keys($input.item.binary)[0];
const binaryBuffer = await this.helpers.getBinaryDataBuffer($itemIndex, binaryPropertyName);
const base64String = binaryBuffer.toString('base64');

const driveFileId = $json.id;
const driveFileName = $json.name;

console.log(`\n=== Google Cloud Vision OCR準備: ${driveFileName} ===`);
console.log(`画像サイズ: ${binaryBuffer.length} bytes`);
console.log(`Base64長: ${base64String.length} 文字`);

// 顧客情報を取得（Bank設定済み顧客フィルタノードから）
const clientData = $('Bank設定済み顧客フィルタ').item.json;

// GCP認証情報を取得（全顧客取得ノードから）
const allClients = $('全顧客取得').all();
const amexTestClient = allClients.find(c => c.json.customer_name === 'amex_test');

if (!amexTestClient || !amexTestClient.json.gcp_service_account_json) {
  throw new Error('GCP認証情報が見つかりません。Master_User_Configのamex_test行にgcp_service_account_jsonを設定してください。');
}

// Vision APIリクエストボディ
const visionRequest = {
  requests: [
    {
      image: {
        content: base64String
      },
      features: [
        {
          type: "DOCUMENT_TEXT_DETECTION"
        }
      ],
      imageContext: {
        languageHints: ["ja"]
      }
    }
  ]
};

return {
  json: {
    visionRequest: visionRequest,
    driveFileId: driveFileId,
    driveFileName: driveFileName,
    client_code: clientData.client_code,
    bank_sheet_id: clientData.bank_sheet_id,
    gcp_service_account_json: amexTestClient.json.gcp_service_account_json
  }
};

// Google Cloud Vision APIをサービスアカウントで認証して呼び出し
const visionRequest = $json.visionRequest;
const driveFileId = $json.driveFileId;
const driveFileName = $json.driveFileName;
const clientCode = $json.client_code;
const amexSheetId = $json.bank_sheet_id;
const gcpServiceAccountJson = $json.gcp_service_account_json;

if (!gcpServiceAccountJson) {
  throw new Error('gcp_service_account_jsonが設定されていません。Master_User_Configシートで設定してください。');
}

// GCPサービスアカウント情報を取得
const serviceAccount = JSON.parse(gcpServiceAccountJson);

// JWT作成（Google Cloud Vision API用）
const crypto = require('crypto');

const now = Math.floor(Date.now() / 1000);
const jwtHeader = Buffer.from(JSON.stringify({
  alg: 'RS256',
  typ: 'JWT'
})).toString('base64url');

const jwtClaim = Buffer.from(JSON.stringify({
  iss: serviceAccount.client_email,
  scope: 'https://www.googleapis.com/auth/cloud-vision',
  aud: 'https://oauth2.googleapis.com/token',
  exp: now + 3600,
  iat: now
})).toString('base64url');

const signatureInput = `${jwtHeader}.${jwtClaim}`;
const signature = crypto.createSign('RSA-SHA256')
  .update(signatureInput)
  .sign(serviceAccount.private_key, 'base64url');

const jwt = `${signatureInput}.${signature}`;

// アクセストークン取得
const tokenResponse = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://oauth2.googleapis.com/token',
  body: {
    grant_type: 'urn:ietf:params:oauth:grant-type:jwt-bearer',
    assertion: jwt
  },
  json: true
});

const accessToken = tokenResponse.access_token;

if (!accessToken) {
  throw new Error(`トークン取得失敗: ${JSON.stringify(tokenResponse)}`);
}

// Vision API呼び出し
const visionResponse = await this.helpers.httpRequest({
  method: 'POST',
  url: 'https://vision.googleapis.com/v1/images:annotate',
  headers: {
    Authorization: `Bearer ${accessToken}`
  },
  body: visionRequest,
  json: true
});

return {
  json: {
    visionResponse: visionResponse,
    driveFileId: driveFileId,
    driveFileName: driveFileName,
    client_code: clientCode,
    bank_sheet_id: amexSheetId
  }
};

// Google Cloud Vision OCRの結果からテキストを抽出
const visionResponse = $json.visionResponse;
const driveFileId = $json.driveFileId;
const driveFileName = $json.driveFileName;
const clientCode = $json.client_code;
const amexSheetId = $json.bank_sheet_id;

console.log(`\n=== OCRテキスト抽出: ${driveFileName} ===`);

// Vision APIレスポンスからテキストを取得
let extractedText = '';

if (visionResponse.responses && visionResponse.responses.length > 0) {
  const response = visionResponse.responses[0];

  if (response.fullTextAnnotation && response.fullTextAnnotation.text) {
    extractedText = response.fullTextAnnotation.text;
    console.log(`✓ テキスト抽出成功: ${extractedText.length}文字`);
    console.log('\n--- OCRテキスト全文 ---');
    console.log(extractedText);
    console.log('--- OCRテキスト終了 ---\n');
  } else if (response.error) {
    throw new Error(`Vision API エラー: ${JSON.stringify(response.error)}`);
  } else {
    throw new Error('テキストが検出されませんでした');
  }
} else {
  throw new Error('Vision APIレスポンスが空です');
}

if (extractedText.length === 0) {
  throw new Error('OCRで抽出されたテキストが空です');
}

return {
  json: {
    extracted_text: extractedText,
    driveFileId: driveFileId,
    driveFileName: driveFileName,
    client_code: clientCode,
    bank_sheet_id: amexSheetId
  }
};

// すべてのGPT-4oレスポンスを取得（Run Once for All Items モード）
const gptItems = $input.all();
const ocrItems = $('OCRテキスト抽出').all();

console.log(`\n=== 取引データ展開開始 ===`);
console.log(`GPT-4oアイテム数: ${gptItems.length}`);
console.log(`OCRアイテム数: ${ocrItems.length}`);

const allTransactions = [];

// 各GPT-4oレスポンスを処理
for (let i = 0; i < gptItems.length; i++) {
  const gptResponse = gptItems[i].json;
  const ocrData = ocrItems[i].json;

  console.log(`\n--- 画像 ${i + 1}/${gptItems.length}: ${ocrData.driveFileName} ---`);

  if (!gptResponse.choices || !gptResponse.choices[0]) {
    console.warn(`⚠️  GPT-4oレスポンスが不正です（画像 ${i + 1}）。スキップします。`);
    continue;
  }

  let content = gptResponse.choices[0].message.content;
  content = content.replace(/^```json\s*\n?/, '').replace(/\n?```\s*$/, '').trim();

  console.log(`GPT-4oレスポンス（最初の500文字）: ${content.substring(0, 500)}`);

  // JSONガード
  if (!content.startsWith('{') && !content.startsWith('[')) {
    console.warn(`⚠️  JSON形式ではないレスポンス（画像 ${i + 1}）。スキップします。`);
    console.warn(`レスポンス: ${content.substring(0, 200)}...`);
    continue;
  }

  let structuredData;
  try {
    structuredData = JSON.parse(content);
  } catch (error) {
    console.warn(`⚠️  JSON解析エラー（画像 ${i + 1}）: ${error.message}。スキップします。`);
    console.warn(`レスポンス: ${content}`);
    continue;
  }

  const transactions = structuredData.transactions || [];
  console.log(`取引データ数: ${transactions.length}件`);

  if (transactions.length === 0) {
    console.warn(`⚠️  取引データが見つかりませんでした（画像 ${i + 1}）。スキップします。`);
    continue;
  }

  // 各取引にメタデータを追加
  for (let j = 0; j < transactions.length; j++) {
    const transaction = transactions[j];
    console.log(`  取引 ${j + 1}: ${transaction.merchant} - ${transaction.amount}円`);

    allTransactions.push({
      json: {
        transaction_date: transaction.transaction_date,
        merchant: transaction.merchant,
        amount: transaction.amount,
        description: transaction.description || '',
        category: transaction.category || '',
        driveUrl: `https://drive.google.com/uc?export=view&id=${ocrData.driveFileId}`,
        driveFileId: ocrData.driveFileId,
        driveFileName: ocrData.driveFileName,
        client_code: ocrData.client_code,
        bank_sheet_id: ocrData.bank_sheet_id
      }
    });
  }
}

console.log(`\n=== 取引データ展開完了 ===`);
console.log(`総取引数: ${allTransactions.length}件`);

if (allTransactions.length === 0) {
  console.warn(`⚠️  処理可能な取引データがありませんでした。`);
  return [];
}

return allTransactions;

// 取引データを取得
const transaction = $input.item.json;
const description = transaction.description || '';
const payerName = transaction.payer_name || '';
const withdrawalAmount = transaction.withdrawal_amount;
const depositAmount = transaction.deposit_amount;

console.log(`\n=== 勘定科目マッチング: ${description} ===`);

// 出金/入金判定
let transactionDirection = '';
let accountSubject = '';
let debitAccount = '';
let creditAccount = '';
let amount = 0;
let matchSource = 'keyword_matching';
let needsAiInference = false;

if (withdrawalAmount !== null && withdrawalAmount > 0) {
  // 出金取引
  transactionDirection = '出金';
  amount = withdrawalAmount;

  // 摘要からキーワードマッチングで勘定科目を推定
  if (description.includes('NTTﾃﾞﾝﾜﾘﾖｳ') || description.includes('ｿﾌﾄﾊﾞﾝｸﾓﾊﾞｲ') || description.includes('ｿﾌﾄﾊﾞﾝｸ') || description.includes('ｺｳｻﾞﾌﾘｶｴ MFﾋﾞﾘﾝｸﾞ')) {
    accountSubject = '通信費';
  } else if (description.includes('ﾃﾞﾝｷﾘﾖｳ') || description.includes('ｶﾞｽﾘﾖｳ') || description.includes('ｽｲﾄﾞｳﾘﾖｳ') || description.includes('ﾄｳｷﾖｳｶﾞｽ')) {
    accountSubject = '水道光熱費';
  } else if (description.includes('ｺﾞﾍﾝｻｲ ｾｲｻｸｺｳｺ') || description.includes('ｺﾞﾍﾝｻｲ')) {
    accountSubject = '長期借入金';
  } else if (description.includes('ﾄﾖﾀﾌｱｲﾅﾝｽ')) {
    accountSubject = '車両費';
  } else if (description.includes('PE ｷﾖｳﾊﾞｼｾﾞｲﾑｼﾖ') || description.includes('PE ﾁﾎｳｾﾞｲ')) {
    accountSubject = '租税公課';
  } else if (description.includes('ﾃｽｳﾘﾖｳ') || description.includes('EBﾃｽｳﾘﾖｳ') || description.includes('ﾌﾘｺﾐﾃｽｳﾘﾖｳ')) {
    accountSubject = '支払手数料';
  } else if (description.includes('ｺｳｻﾞﾌﾘｶｴ ｱﾒﾘｶﾝｴｷｽﾌﾟﾚｽ')) {
    accountSubject = '未払金';
  } else if (description.includes('ﾔﾁﾝ')) {
    accountSubject = '地代家賃';
  } else if (description.includes('ｷﾕｳﾖ') || description.includes('ｷﾕｳﾖｳ')) {
    accountSubject = '給与';
  } else {
    // マッチしない場合はAI推論が必要
    needsAiInference = true;
    accountSubject = 'Review_Required';
    matchSource = 'pending';
  }

  // 借方・貸方設定（出金）
  debitAccount = accountSubject;
  creditAccount = '普通預金';

} else if (depositAmount !== null && depositAmount > 0) {
  // 入金取引
  transactionDirection = '入金';
  amount = depositAmount;

  if (description.includes('ﾌﾘｺﾐ') && (payerName.includes('ｶ)') || payerName.includes('(ｶ'))) {
    // 法人からの振込 → 売掛金回収
    accountSubject = '売掛金';
  } else if (description.includes('ﾌﾘｺﾐ')) {
    // その他の振込 → 雑収入
    accountSubject = '雑収入';
  } else if (description.includes('ﾘｿｸ')) {
    accountSubject = '受取利息';
  } else {
    needsAiInference = true;
    accountSubject = 'Review_Required';
    matchSource = 'pending';
  }

  // 借方・貸方設定（入金）
  debitAccount = '普通預金';
  creditAccount = accountSubject;
}

console.log(`${description}: ${matchSource} → ${accountSubject}`);

return {
  json: {
    ...transaction,
    transactionDirection: transactionDirection,
    category: accountSubject,
    debitAccount: debitAccount,
    creditAccount: creditAccount,
    amount: amount,
    match_source: matchSource,
    needs_ai_inference: needsAiInference
  }
};

// 取引情報を取得
const transaction = $input.item.json;

// AI推論用プロンプト構築
const merchant = transaction.merchant || '不明';
const description = transaction.description || '詳細不明';
const amount = transaction.amount || 0;

const prompt = `以下の取引から適切な勘定科目を推定してください。

店舗名: ${merchant}
取引内容: ${description}
金額: ${amount}円

勘定科目の選択肢:
- 消耗品費
- 旅費交通費
- 会議費
- 交際費
- 通信費
- 水道光熱費
- 広告宣伝費
- 研修費
- 新聞図書費
- 車両費
- 雑費

JSON形式で出力してください:
{
  "category": "推定した勘定科目",
  "tax_rate": 10
}`;

const requestBody = {
  model: "gpt-4o-mini",
  messages: [
    {
      role: "system",
      content: "あなたは経理の専門家AIです。取引内容から適切な勘定科目を推定してください。出力は必ずJSON形式のみとし、説明文は一切含めないでください。"
    },
    {
      role: "user",
      content: prompt
    }
  ],
  max_tokens: 200,
  temperature: 0
};

return {
  json: {
    requestBody: requestBody,
    transaction: transaction
  }
};

// AI推論結果をパース
let content = $input.item.json.choices[0].message.content;

// マークダウンコードブロックを除去
content = content.replace(/^```json\s*\n?/, '').replace(/\n?```\s*$/, '').trim();

// 説明文を除去（JSONの前にある説明文を削除）
const jsonMatch = content.match(/\{[\s\S]*\}/);
if (jsonMatch) {
  content = jsonMatch[0];
}

let aiResult;
try {
  aiResult = JSON.parse(content);
} catch (error) {
  throw new Error(`JSON解析エラー: ${error.message}\n元のレスポンス: ${content}`);
}

// AI推論リクエスト構築ノードから元の取引データを取得
const transaction = $('AI推論リクエスト構築').item.json.transaction;

return {
  json: {
    ...transaction,
    category: aiResult.category,
    tax_rate: aiResult.tax_rate || 10,
    match_source: 'ai_inference',
    matched_confidence: 0,
    needs_ai_inference: false
  }
};

// 勘定科目判定結果を取得
const judgmentResult = $input.item.json;

// 消費税率の判定（軽減税率対応）
let taxRate = 10; // デフォルトは標準税率
const merchant = judgmentResult.merchant || '';

// 軽減税率（8%）対象店舗
const reducedTaxMerchants = [
  'セブン', 'ローソン', 'ファミマ', 'ファミリーマート',
  'イオン', '西友', 'UBER EATS', 'Uber Eats', '出前館'
];

for (const keyword of reducedTaxMerchants) {
  if (merchant.includes(keyword)) {
    taxRate = 8;
    break;
  }
}

// 税区分の判定
const taxType = taxRate === 10 ? "課10%" : "課8%";

return {
  json: {
    ...judgmentResult,
    tax_rate: taxRate,
    tax_type: taxType
  }
};

// 全取引の記帳完了後、処理済みマーキング用のデータを準備
// OCRテキスト抽出ノードから全画像を取得（取引の有無に関わらず）

console.log('\n=== リネーム準備 ===');

// OCRテキスト抽出ノードから全画像を取得
const ocrItems = $('OCRテキスト抽出').all();

if (!ocrItems || ocrItems.length === 0) {
  throw new Error('OCRテキスト抽出ノードからデータを取得できませんでした');
}

console.log(`OCR処理済み画像数: ${ocrItems.length}件`);

// driveFileIdでグループ化してユニークなファイルリストを作成
const fileMap = new Map();

for (const item of ocrItems) {
  const driveFileId = item.json.driveFileId;
  const driveFileName = item.json.driveFileName;

  if (driveFileId && driveFileName && !fileMap.has(driveFileId)) {
    fileMap.set(driveFileId, driveFileName);
  }
}

console.log(`ユニークなファイル数: ${fileMap.size}件`);

// 各ファイルを個別のアイテムとして返す
const results = [];
for (const [driveFileId, driveFileName] of fileMap.entries()) {
  console.log(`  - ${driveFileName} (ID: ${driveFileId.substring(0, 20)}...)`);
  results.push({
    json: {
      driveFileId: driveFileId,
      driveFileName: driveFileName
    }
  });
}

console.log(`\nリネーム対象ファイル数: ${results.length}件`);
console.log('取引の有無に関わらず、OCR処理が完了した全ファイルをリネームします。\n');

return results;
